import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';
import Link from '@material-ui/core/Link';
import Container from '@material-ui/core/Container';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import {API_ROOT} from "../constants";
import {useState, useEffect} from 'react';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        maxWidth: '100%',
        flexGrow:1,
    },
    gridItem:{
        width:'50%',
    },
    gridList: {
        flexWrap: 'nowrap',
        transform: 'translateZ(0)',
    },
    title:{
        textAlign:'left',
        fontWeight:'bolder',
        fontSize:'10',
    },
    item:{
        textAlign:'left',
        fontSize:'10',
    }
}));

function getItems(setItems) {
    fetch(`${API_ROOT}/post`, {
        method: 'GET',
    })
        .then((response) => {
            if (response.ok) {
                return response.json();
            }
            throw new Error('Failed to load post');
        })
        .then(data => {
            setItems(data);
        }).catch((e) => {
        console.error(e);
    });
}
function getImgs(setItems) {
    fetch(`${API_ROOT}/posts`, {
        method: 'GET',
    })
        .then((response) => {
            if (response.ok) {
                return response.json();
            }
            throw new Error('Failed to load post');
        })
        .then(data => {
            setItems(data);
        }).catch((e) => {
        console.error(e);
    });
}
export default function Item() {
    const [post, setPost] = useState("");
    const[imgs, setImgs] = useState([]);
    const classes = useStyles();
    const space = 1;
    useEffect(() => {
        getItems(setPost);
        getImgs(setImgs);
    });
    return (
        <Container component="main" maxWidth="l">
            <div >
                <Grid container >
                    <Link
                        component="button"
                        onClick={() => {
                            getItems(setPost);
                        }}
                    >
                        Back to result
                    </Link>
                </Grid>
                <Divider variant="middle" />
                <br></br>
                <Grid container spacing={space} >
                    <Grid item className={classes.gridItem} spacing={space}>
                        <GridList className={classes.gridList} cols={2.5}>
                            {
                                imgs.map((tile) => (
                                    <GridListTile key={tile.img}>
                                        <img src={tile.img}/>
                                        <GridListTileBar
                                            classes={{
                                                root: classes.titleBar,
                                                title: classes.title,
                                            }}

                                        />
                                    </GridListTile>
                                ))}
                        </GridList>
                    </Grid>
                    <Grid item className={classes.gridItem} >
                        <div className={classes.title}>{post.title}</div>
                        <Grid item className={classes.gridItem} spacing={space}>
                            <div className={classes.item}>Seller: {post.user}</div>
                            <div className={classes.item}>Relaase: {post.data}</div>
                        </Grid>
                        <Divider  />
                        <Grid item className={classes.gridItem} spacing={space}>
                        </Grid>
                    </Grid>
                </Grid>
                <Grid container spacing={space}>
                    <Grid item xs>
                       
                    </Grid>
                    <Grid item xs={6}>
                       
                    </Grid>
                    <Grid item xs>
                        
                    </Grid>
                </Grid>
                <Grid container spacing={2}>
                    <Grid>
                        <Grid item xs>
                        </Grid>

                    </Grid>
                    <Grid> <Grid item xs>
                        <h6>item2</h6>
                    </Grid>
                        <Grid item xs>
                            <h6>item1</h6>
                        </Grid>
                        <Grid item xs>
                            <h6>item2</h6>
                        </Grid>
                    </Grid>

                </Grid>


            </div>

        </Container>

    );
}
