import React from "react";
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Divider from '@material-ui/core/Divider';
import Link from '@material-ui/core/Link';
import Container from '@material-ui/core/Container';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import StarBorderIcon from '@material-ui/icons/StarBorder';
import {API_ROOT} from "../constants";
import {useState, useEffect} from 'react';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        maxWidth: '100%',
    },
    gridItem:{
        width:'50%',
    },
    gridList: {
        flexWrap: 'nowrap',
        // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
        transform: 'translateZ(0)',
    },
    title: {
        color: theme.palette.primary.light,
    },
    titleBar: {
        background:
            'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
    textleft:{
        textAlign:'left',
    }
}));

function getItems(setItems) {
    fetch(`${API_ROOT}/post`, {
        method: 'GET',
    })
        .then((response) => {
            if (response.ok) {
                return response.json();
            }
            throw new Error('Failed to load post');
        })
        .then(data => {
            setItems(data);
        }).catch((e) => {
        console.error(e);
    });
}
function getImgs(setItems) {
    fetch(`${API_ROOT}/posts`, {
        method: 'GET',
    })
        .then((response) => {
            if (response.ok) {
                return response.json();
            }
            throw new Error('Failed to load post');
        })
        .then(data => {
            setItems(data);
        }).catch((e) => {
        console.error(e);
    });
}
export default function Item() {
    const [post, setPost] = useState("");
    const[imgs, setImgs] = useState([]);
    const classes = useStyles();
    const space = 1;
    useEffect(() => {
        getItems(setPost);
        getImgs(setImgs);
    });
    return (
        <Container component="main" maxWidth="l">
            <div >
                <Grid container alignItems="center" spacing={space}>
                    <Link
                        component="button"
                        onClick={() => {
                            getItems(setPost);
                        }}
                    >
                        Back to result
                    </Link>
                </Grid>
                <Divider variant="middle" />
                <Grid container spacing={1}>
                    <Grid item className={classes.gridItem} spacing={space}>
                        <GridList className={classes.gridList} cols={2.5}>
                            {
                                imgs.map((tile) => (
                                    <GridListTile key={tile.img}>
                                        <img src={tile.img}/>
                                        <GridListTileBar
                                            classes={{
                                                root: classes.titleBar,
                                                title: classes.title,
                                            }}

                                        />
                                    </GridListTile>
                                ))}
                        </GridList>
                    </Grid>
                    <Grid item className={classes.gridItem} spacing={space}>
                        <h4 className={classes.textleft}>{post.title}</h4>
                        <Grid item className={classes.gridItem} spacing={space}>
                            Seller: {post.user}
                        </Grid>
                        <Grid item className={classes.gridItem} spacing={space}>
                            Seller: {post.user}
                        </Grid>
                    </Grid>
                </Grid>
                <Grid container spacing={space}>
                    <Grid item xs>
                        <Paper className={classes.paper}>xs</Paper>
                    </Grid>
                    <Grid item xs={6}>
                        <Paper className={classes.paper}>xs=6</Paper>
                    </Grid>
                    <Grid item xs>
                        <Paper className={classes.paper}>xs</Paper>
                    </Grid>
                </Grid>
                <Grid container spacing={2}>
                    <Grid>
                        <Grid item xs>
                        </Grid>

                    </Grid>
                    <Grid> <Grid item xs>
                        <h6>item2</h6>
                    </Grid>
                        <Grid item xs>
                            <h6>item1</h6>
                        </Grid>
                        <Grid item xs>
                            <h6>item2</h6>
                        </Grid>
                    </Grid>

                </Grid>


            </div>

        </Container>

    );
}
